import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';

import clienteApi from "../../services/clienteApi";

function Clientes () {


    const [ data, setData ] = useState([]);

    useEffect(() => {

        let response = {}

        async function loadData(){

            response = await clienteApi.all();
            setData(response.data.data);

        }

        loadData();

    }, [])


    const onEdit = (item) => {
       alert(item.id);
    }

    const onDelete = (id) => {
        alert(id);
    }


    return (

        <div className="content-wrapper">
            <section className="content-header">
                <h1>
                    Clientes
                    <small>Seja bem vindo...</small>
                </h1>
                <ol className="breadcrumb">
                    <li><Link to="/dashboard"><i className="fa fa-dashboard"></i> Home</Link></li>
                    <li className="active">Cliente</li>
                </ol>
            </section>

            <section className="content">
                <div className="row">
                    <div className="col-xs-12">
                        <div className="box">
                            <div className="box-header">
                                <h3 className="box-title">Clientes</h3>
                            </div>

                            <div className="box-body">
                                <Link to="/clientes" type="button" className="btn btn-success">Novo</Link>
                                <br /><br />
                                <DataTable data={data} onEdit={onEdit} onDelete={onDelete}/>
                                <DataForm />
                            </div>
                        </div>

                    </div>

                </div>

            </section>
        </div>
    );

}

export default Clientes;

function DataForm(){
    return (
        <form action="">

        </form>
    )
}

function DataTable( info ){

    const { data } = info;
    console.log(data)

    const onEdit = (item) => {
       return info.onEdit(item);
    }

    const onDelete = (id) => {
        return info.onDelete(id);
    }

    return (
        <table className="table table-bordered table-hover table-condensed">
            <thead>
                <tr>
                   <th>#</th>
                   <th>Nome</th>
                   <th>Email</th>
                   <th>Telefone</th>
                   <th>Ações</th>
                </tr>
            </thead>
            <tbody>
            {
                data.map(item => (
                    <tr key={item.id}>
                        <td>{item.id}</td>
                        <td>{item.nome}</td>
                        <td>{item.email}</td>
                        <td>{item.telefone}</td>
                        <td>
                            <button onClick={e => onEdit(item)} className="btn btn-primary btn-sm">Editar</button>
                            <button onClick={e => onDelete(item.id)} className="btn btn-danger btn-sm">Deletar</button>
                        </td>
                    </tr>
                ))
            }
            </tbody>
            <tfoot>

            </tfoot>
        </table>
    )
}
