import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';

import Table from "../../components/Table";

import clienteApi from "../../services/clienteApi";

function Clientes () {

    const header = ['ID','Nome','Email', 'Telefone'];
    const [ data, setData ] = useState([]);

    useEffect(() => {

        let response = {}

        async function loadData(){

            const response = await clienteApi.all();
            setData(response.data.data);

        }

        loadData();

    }, [])

    return (

        <div className="content-wrapper">
            <section className="content-header">
                <h1>
                    Clientes
                    <small>Seja bem vindo...</small>
                </h1>
                <ol className="breadcrumb">
                    <li><Link to="/dashboard"><i className="fa fa-dashboard"></i> Home</Link></li>
                    <li className="active">Cliente</li>
                </ol>
            </section>

            <section className="content">
                <div className="row">
                    <div className="col-xs-12">
                        <div className="box">
                            <div className="box-header">
                                <h3 className="box-title">Clientes</h3>
                            </div>

                            <div className="box-body">
                                <Link to="/clientes" type="button" className="btn btn-success">Novo</Link>
                                <br /><br />
                                <DataTable data={data} header={header}/>
                            </div>
                        </div>

                    </div>

                </div>

            </section>
        </div>
    );

}

export default Clientes;

function DataForm(){
    return (
        <form action="">

        </form>
    )
}

function DataTable({ data, header }){

    console.log(data)

    return (
        <table>
            <thead>
                <th>
                    {
                        header.map((head, index) => {
                            <th key={index}>{head}</th>
                        })
                    }
                </th>
            </thead>
            <tbody>

            </tbody>
            <tfoot>

            </tfoot>
        </table>
    )
}
