import axios from 'axios';

const resource = 'cliente';

const clienteApi = axios.create({
    baseURL: 'http://localhost:8080',
    headers: {
        'Content-Type': 'application/json',
        'Accept' : 'application/json',
        'Authorization': 'Bearer '.concat(localStorage.getItem('token'))}
});

export const _all = () => {
    return clienteApi.get(`/${resource}`);
}

export const _find = (id) => {
    return clienteApi.get(`/${resource}/${id}`)
}

export const _create = (data) => {
    return clienteApi.post(`/${resource}`, data)
}

export const _delete = (id) => {
    return clienteApi.delete(`/${resource}/${id}`)
}

export const _update = (id, data) => {
    return clienteApi.put(`/${resource}/${id}`, data)
}

export default {
    all,
    find,
    create,
    destroy,
    update
}
