import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';

import clienteApi from "../../services/clienteApi";

function Clientes () {


    const [ data, setData ] = useState([]);
    const [ tableVisible, setTableVisible ] = useState(true);

    useEffect(() => {

        let response = {}

        async function loadData(){

            response = await clienteApi.all();
            setData(response.data.data);

        }

        loadData();

    }, [])


    const onCreate = () => {
        setTableVisible(false);
    }

    const onEdit = (item) => {
       alert('edit' + item.id);
    }

    const onDelete = (id) => {
        alert('delete' + id);
    }

    const onSubmit = (item) => {
        alert('salvar' + item.id);
    }

    return (

        <div className="content-wrapper">
            <section className="content-header">
                <h1>
                    Clientes
                    <small>Seja bem vindo...</small>
                </h1>
                <ol className="breadcrumb">
                    <li><Link to="/dashboard"><i className="fa fa-dashboard"></i> Home</Link></li>
                    <li className="active">Cliente</li>
                </ol>
            </section>

            <section className="content">
                <div className="row">
                    <div className="col-xs-12">

                        { tableVisible ?
                            <DataTable data={data} onEdit={onEdit} onDelete={onDelete} onCreate={onCreate}/>
                        :
                            <DataForm onSubmit={onSubmit}/>
                        }

                    </div>

                </div>

            </section>
        </div>
    );

}

export default Clientes;

function DataForm( info ){

    const onSubmit = (item) => {
        return info.onSubmit(item);
    }

    return (

        <div className="box">
            <div className="box-header">
                <h3 className="box-title">Dados do Cliente</h3>
            </div>

            <div className="box-body">
                <form role="form">
                    <div className="box-body">
                        <div className="form-group">
                            <label htmlFor="nome">Nome</label>
                            <input type="text" className="form-control" id="nome" placeholder="Nome" />
                        </div>
                        <div className="form-group">
                            <label htmlFor="email">Password</label>
                            <input type="email" className="form-control" id="email" placeholder="Email" />
                        </div>
                        <div className="form-group">
                            <label htmlFor="telefone">Password</label>
                            <input type="text" className="form-control" id="telefone" placeholder="Telefone" />
                        </div>
                    </div>

                    <div className="box-footer">
                        <button type="submit" className="btn btn-primary">Salvar</button>
                    </div>
                </form>
            </div>
        </div>

    )
}

function DataTable( info ){

    const { data } = info;
    console.log(data)

    const onCreate = () => {
        return info.onCreate();
    }

    const onEdit = (item) => {
       return info.onEdit(item);
    }

    const onDelete = (id) => {
        return info.onDelete(id);
    }

    return (

        <div className="box">
            <div className="box-header">
                <h3 className="box-title">Clientes</h3>
            </div>

            <div className="box-body">
                <button onClick={onCreate} type="button" className="btn btn-success">Novo</button>
                <br /><br />
                <table className="table table-bordered table-hover table-condensed">
                    <thead>
                    <tr>
                        <th>#</th>
                        <th>Nome</th>
                        <th>Email</th>
                        <th>Telefone</th>
                        <th>Ações</th>
                    </tr>
                    </thead>
                    <tbody>
                    {
                        data.map(item => (
                            <tr key={item.id}>
                                <td>{item.id}</td>
                                <td>{item.nome}</td>
                                <td>{item.email}</td>
                                <td>{item.telefone}</td>
                                <td>
                                    <button onClick={e => onEdit(item)} className="btn btn-primary btn-sm">Editar</button>
                                    <button onClick={e => onDelete(item.id)} className="btn btn-danger btn-sm">Deletar</button>
                                </td>
                            </tr>
                        ))
                    }
                    </tbody>
                    <tfoot>

                    </tfoot>
                </table>
            </div>
        </div>



    )
}
